import React, { useState } from 'react'
import Heading from './Heading.jsx'







function App(){

	const [bizNames, setBizNames] = useState('click below to get started!')

	function getRandoNum(min, max){
		max = max + 1
		min = Math.ceil(min);
		max = Math.floor(max);
		return Math.floor(Math.random() * (max - min)) + min;
	}

	async function generateName(){
		let and_sign = getRandoNum(0, 1)
		if (and_sign == 0){
			and_sign = "+"
		} else {
			and_sign = "&"
		}

		let word1
		let word2

		fetch('https://random-word-api.herokuapp.com/word?number=5')
		.then(res => res.json())
		.then(async(randomwords) => {

			let topics = `${randomwords[0]},poop,${randomwords[1]},${randomwords[2]},fart}`

			const response = await fetch(`https://api.datamuse.com/words?topics=${topics}&max=200&md=ps`)
			const data = await response.json()
			console.log(data[0])

			for(let i = 0; i<data.length; i++){
				if (data[i].numSyllables == 2 && data[i].tags.includes('n')){
					word1 = data[i].word
					console.log(word1)
					return
				}
			}

			topics = `rat,pus,pee,${randomwords[3]},${randomwords[4]}}`

			const response2 = await fetch(`https://api.datamuse.com/words?topics=${topics}&max=200&md=ps`)
			const data2 = await response2.json()

			for(let i = 0; i<data.length; i++){
				if (data2[i].numSyllables == 1 && data2[i].tags.includes('n')){
					word2 = data2[i].word
					console.log(word2)
					setBizNames(word1 + " " + and_sign + " " + word2 )


					return
				}
			}

		})





	}

	return (<>
		<Heading/>
		<div>
			<div className="test" onClick={generateName}>{bizNames}</div>
		</div>
	</>)
}

export default App;
