import React, { useState } from 'react'
import Heading from './Heading.jsx'







function App(){

	const [bizNames, setBizNames] = useState('click below to get started!')

	function getRandoNum(min, max){
		max = max + 1
		min = Math.ceil(min);
		max = Math.floor(max);
		return Math.floor(Math.random() * (max - min)) + min;
	}

	async function generateName(){
		let and_sign = getRandoNum(0, 1)
		if (and_sign == 0){
			and_sign = "+"
		} else {
			and_sign = "&"
		}

		let word1
		let word2

		fetch('https://random-word-api.herokuapp.com/word?number=5')
		.then(res => res.json())
		.then((randomwords) => {

			let topics = `${randomwords[0]},poop,${randomwords[1]},${randomwords[2]},fart}`

			fetch(`https://api.datamuse.com/words?topics=${topics}&max=200&md=ps`)
			.then(res => res.json())
			.then((data) => {

				for(let i = 0; i<data.length; i++){
					if (data[i].numSyllables == 2 && data[i].tags.includes('n')){
						word1 = data[i].word
						console.log(word1)
						return
					}
				}
			})

			topics = `${randomwords[3]},rat,${randomwords[4]},pus,pee}`

			fetch(`https://api.datamuse.com/words?topics=${topics}&max=200&md=ps`)
			.then(res => res.json())
			.then((data) => {

				for(let i = 0; i<data.length; i++){
					if (data[i].numSyllables == 1 && data[i].tags.includes('n')){
						word2 = data[i].word
						console.log(word2)
						return
					}
				}


			})
		})
		.then(console.log(word1))



		console.log(and_sign)
		setBizNames(and_sign)

	}

	return (<>
		<Heading/>
		<div>
			<div className="test" onClick={generateName}>{bizNames}</div>
		</div>
	</>)
}

export default App;
