import React, { useState } from 'react'
import Heading from './Heading.jsx'







function App(){

	const [bizNames, setBizNames] = useState('click below to get started!')

	function getRandoNum(min, max){
		max = max + 1
		min = Math.ceil(min);
		max = Math.floor(max);
		return Math.floor(Math.random() * (max - min)) + min;
	}

	async function generateName(){
		let and_sign = getRandoNum(0, 1)
		if (and_sign == 0){
			and_sign = "+"
		} else {
			and_sign = "&"
		}

		fetch('https://random-word-api.herokuapp.com/word?number=3')
		.then(res => res.json())
		.then((data) => {
			console.log(data)

			let topics = `${data[0]},${data[1]},${data[2]},poop,fart}`

			fetch(`https://api.datamuse.com/words?topics=${topics}&max=30&md=ps`)
			.then(res => res.json())
			.then((data) => {

				console.log(data)

				data.forEach(function(word){
					if (word.numSyllables == 2 && word.tags.includes('n')){
						console.log(word.word)
					}
				})


			})
		})



		console.log(and_sign)
		setBizNames(and_sign)

	}

	return (<>
		<Heading/>
		<div>
			<div className="test" onClick={generateName}>{bizNames}</div>
		</div>
	</>)
}

export default App;
