import React, { useState } from 'react'
import Heading from './Heading.jsx'







function App(){

	const [bizNames, setBizNames] = useState('click below to get started!')

	function getRandoNum(min, max){
		max = max + 1
		min = Math.ceil(min);
		max = Math.floor(max);
		return Math.floor(Math.random() * (max - min)) + min;
	}

	async function generateName(){
		let and_sign = getRandoNum(0, 1)
		if (and_sign == 0){
			and_sign = "+"
		} else {
			and_sign = "&"
		}

		let word1
		let word2

		fetch('https://random-word-api.herokuapp.com/word?number=6')
		.then(res => res.json())
		.then((data) => {

			let topics = `${data[0]},poop,${data[1]},${data[2]},fart}`

			fetch(`https://api.datamuse.com/words?topics=${topics}&max=200&md=ps`)
			.then(res => res.json())
			.then((data) => {

				data.forEach(function(word){
					if (word.numSyllables == 2 && word.tags.includes('n')){
						word1 = word.word
						return
					}
				})
			})

			topics = `${data[3]},pus,${data[4]},${data[5]},pee}`

			fetch(`https://api.datamuse.com/words?topics=${topics}&max=200&md=ps`)
			.then(res => res.json())
			.then((data) => {

				data.forEach(function(word){
					if (word.numSyllables == 1 && word.tags.includes('n')){
						word2 = word.word
						console.log(`${word1} ${word2}`)
						return
					}
				})
			})
		})



		console.log(and_sign)
		setBizNames(and_sign)

	}

	return (<>
		<Heading/>
		<div>
			<div className="test" onClick={generateName}>{bizNames}</div>
		</div>
	</>)
}

export default App;
